userStyleRules:
/* Show title of unread tabs with red and italic font */
.tab.unread .label {
  font-style: italic;
  color: red;
}


/* Add private browsing indicator per tab */
.tab.private-browsing .label:before {
  content: "üï∂";
}


/* Necessary CSS for counting tabs */
#tabbar {
  counter-reset: vtabs atabs tabs;
  /* vtabs tracks visible tabs, atabs tracks active tabs, tabs tracks all tabs */
}
.tab:not(.collapsed):not(.discarded) {
  counter-increment: vtabs atabs tabs;
}
.tab:not(.collapsed) {
  counter-increment: vtabs tabs;
}
.tab:not(.discarded) {
  counter-increment: atabs tabs;
}
.tab {
  counter-increment: tabs;
}

/* Numbering of tabs */
.tab .extra-items-container {
  z-index: unset !important;
}
.tab .extra-items-container::after {
  color: blue;
  content: counter(vtabs);
  left: 0;
  padding: 0.2em;
  pointer-events: none;
  position: absolute;
  z-index: 1000;
}

/* Only show numbering on hovered tabs */
.tab .extra-items-container::after {
  opacity: 0;
  transition: 0.2s;
}
.tab:hover .extra-items-container::after {
  opacity: 1;
}


/* Necessary CSS for displaying numbering on new tab button */
.newtab-button::after {
  content: var(--tab-count-text);
  pointer-events: none;

  position: absolute;
  left: 0.5em;

  /* TST 2.4.0 - Fix for Issue #1664 */
  background: transparent !important;
  mask: none !important;
}

/* Tab counts in new tab button */
.newtab-button {
  --tab-count-text: counter(tabs) " tabs";
}
.newtab-button {
  --tab-count-text: counter(atabs) "/" counter(tabs) " tabs";
}

/* Put "new tab" button at the top of the tab bar */
.newtab-button-box {
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  height: 20px;
}
#tabbar {
  top: 20px !important;
  bottom: 0 !important;
}
.tab.pinned {
  margin-top: 20px;
}


/* Only show tab close button on hover */
.tab:not(:hover) .closebox {
  display: none;
}


/* Close boxes style */
.closebox::after {
  content: "‚ùå";
  background: none;
  line-height: 1;
  mask: none;
  text-align: center;
  width: 1.5em;
}

/* Show scrollbar in the tab bar rightside */
:root.left #tabbar {
  direction: ltr;
  overflow-x: hidden;
}

/* last active tab */
.tab.last-active {
  background-color: lightcyan !important;
}
