/*=============论坛悬浮回复框，延时收缩版 by defpt at 2014.01.11=======================================*/

@-moz-document regexp(".*forum.*"), regexp(".*thread.*"), regexp(".*tid=.*"), regexp(".*read.*tid-.*"), regexp(".*bbs.*") {
/*底部触发*/
#anchor,#quickpost, #f_post, #f_pst, #fast_post_c,#fastpostform {
        position:fixed !important;
        border:1px solid #51AFF7 !important;
        left: auto !important;
        width: 80% !important;
        bottom: 0 !important;
        max-height: 3px !important;
        padding:0 !important;
        margin:0 !important;     
        background:#F9F9F9 !important;
        z-index:99 !important;
        transition-duration:0.5s !important;/*收缩时间，单位为秒*/
        transition-delay:1.5s !important;/*收缩延时，单位为秒*/
    }
    #anchor:hover,#quickpost:hover, #f_pst:hover, #f_post:hover, #fast_post_c:hover,#fastpostform:hover{
        max-height: 800px !important; 
        height:auto !important;
        transition-duration: 0.1s !important;/*弹出时间，单位为秒*/
        transition-delay: 0.5s !important;/*弹出延时，防止误触发，单位为秒*/
    }
    #f_pst .p_pop, #f_pst .p_opt{
        position: absolute !important;
        bottom: 0 !important;
        top: auto !important;
    }
}

@-moz-document domain("g.mozest.com") {
