.jmd a,.jmd_base a{
  border:none!important;
  display: inline-block!important;
  margin:2px 8px!important;
}
#mini_jmd{
  position: relative!important;
  left:0px;
  top:0px;
  width:960px!important;
  padding-top:30px;
}
#mini_jmd .fr a{
  background:url("data:image/png;base64,")no-repeat right top;
  display: block!important;
  height:70px;
  width:90px;
  position: absolute;
  top:-3px;
  right:-3px;
  text-indent:-999em!important;
  visibility: visible!important;
}
#mini_jmd .nav_title{
  visibility: hidden!important;
  height:0px!important;
}
.jmd th,.jmd_base th{
  background: none!important;
  color:#000!important;
  white-space:nowrap;
  padding-right:20px!important;
}
.jmd tr:hover td,
.jmd tr:hover th,
.jmd_base tr:hover td ,
.jmd_base tr:hover th{
    background:#E4EFFC!important;
  }

/***********每周番组,完结番组***********/
#cms-13,#cms-65{
  margin:10px auto!important;
}
.weekly_list_title a,.bgm-anime-title a{
  color:#fff!important;
}
.weekly_list td+td{
  width:885px;
}
#tue+td,.bgm-rowhead-tue+td{background:#66F;}
#wed+td,.bgm-rowhead-wed+td{background:#800;}
#thu+td,.bgm-rowhead-thu+td{background:#c60;}
#fri+td,.bgm-rowhead-fri+td{background:#990;}
#sat+td,.bgm-rowhead-sat+td{background:#006;}
#sun+td,.bgm-rowhead-sun+td{background:#360;}
#mon+td,.bgm-rowhead-mon+td{background:#906;}
#long+td{background:#333;}
/***********主菜单***********/
.top_sort{
  position: relative!important;
  top:40px;
  left:-4px;
  width:980px;
  margin:0 auto;
  border-radius: 2px;
}
.top_sort .menu{
  border-radius: 2px;
  height: 40px;
  width:730px;
  background: linear-gradient(#FFF, #E1E1E1) repeat scroll 0% 0% transparent;
  padding-right:250px;
}
.top_sort .menu ul{
  line-height: 40px!important;
}
.top_sort .nav li a:hover,.top_sort .nav ul a{
  background:#F4F4F4!important;
  text-decoration: none!important;
}
.top_sort .nav ul li:hover a,.top_sort .nav ul li:hover font{
  display: block;
  background-color: #333537!important;;
  text-shadow: 0px -1px 1px rgba(0, 0, 0, 0.5);
  box-shadow: 0px 0px 2px rgba(0, 0, 0, 0.75);
  border-radius: 3px;
  color: #FFF !important;
  text-decoration: none!important;
 }
.menu li{
  border-right: 1px solid #CCC;
  height: 40px;
}
.menu li font{
  color:#000!important;
}
.menu .sort-2:before,.menu .sort-3:before,.menu .sort-4:before,.menu .sort-9:before{
  content:"▼";
  font-size:10px;
  color:#000;
  padding-right:10px;
}
/********联盟********/
#expand-button{
  position: absolute;
  display: block;
  top: 7px;
  right: 10px;
  width:50px!important;
  background:linear-gradient(#FFF, #E1E1E1) repeat scroll 0% 0% transparent!important;
  padding:0px!important;
  margin:0!important;
  border:1px solid #bbb;
}
#expand-button div{
  margin:0!important;
}
#team-dialog{
  margin-top:40px;
  margin-left:0px;
}
#team-inner {
  width:960px!important;
}
/******************表格******************/
.odd,.even,.odd td,.even td{
  background-color:#fafafa!important;
}  
.odd:hover td,.even:hover td{
  background-color:#f2f2f2!important;
}
#topic_list{
  border-collapse: collapse;
  border-spacing: 0px; 
  width:980px!important;
  margin-left:-10px!important;
}
.odd,.even{
  border-bottom: 1px solid #ddd!important;
}

#topic_list th{
  height:24px!important;
  padding: 0!important;
  color:#000;
  font-weight: normal;
  border-bottom: 1px solid #DDD;
  background: none repeat scroll 0% 0% #EEE!important;
}
#topic_list~.nav_title{
  float:none!important;
  text-align: center!important;
}
/************小标题************/
img[alt="rss"]{
  vertical-align: middle!important;
}
.table .nav_title,#cms-13 .nav_title,#cms-65 .nav_title{
  border-bottom: 1px solid #AAA;
  font-weight: bold;
  color: #1A7BB9;
  height: 32px;
  line-height: 32px;
  margin: -10px -10px 0 -10px;
  padding: 0px 10px;
  font-size: 14px;
  background: linear-gradient(#FFF, #E1E1E1) repeat scroll 0% 0% transparent; 
} 
#topic_list~.nav_title{
  margin-bottom: -10px!important;
  border-top:1px solid #eee
}
/**********详情页***********/
.topics_bk{
  width:958px;
}
.topic-nfo img{
  max-width:760px!important;
}
/********页脚********/
.footer{
  background:linear-gradient(#105D7A, #023243) repeat scroll 0% 0% transparent;
  padding-bottom: 60px;
  margin: 0px;
} 
.footer+div,.footer+div+div{
  position:absolute;
  bottom:20px;
  width: 100%;
}
.top a{
  color:#fff!important;
}
.footer:after{
  content:"仿极影 by 天涯倦客";
  font-size: 12px;
  color:#4C9ED9;
}
/*******广告*******/
.ad,[id$="_ad"],[id^="id01_"],#before-comment,#DM_ad+br,a[name="description-end"]+br{
    display:none!important;
  }
}

